static entity_sides
	sound_system_side_loaded = ENTITY_SIDE_NEUTRAL;

/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////

void load_application_sound_samples ( void )
{

	if ( application_sound_system_enabled )
	{

		if ( !command_line_no_sound && !command_line_comms_dedicated_server )
		{

			number_of_application_sound_samples = ( sizeof ( application_sound_samples ) / sizeof ( sound_sample_information ) );

			allocate_source_sound_samples ( NUM_SOUND_SAMPLE_INDICES + 1 );

			load_side_dependant_application_sound_samples ( ENTITY_SIDE_NEUTRAL );
		}
	}
}

/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////


static void load_from_file ( int sound_sample_index, int type, int rate, int size, FILE* fp )
{
	unsigned char
		*ptr;

	application_sound_effects[sound_sample_index].rate = rate * ( type == SAMPLE_TYPE_MONO_16BIT ? 2 : 1 );
	application_sound_effects[sound_sample_index].size = size;

	create_source_sound_sample ( sound_sample_index, ( sample_types ) type, rate, size );
	ptr = ( unsigned char * ) safe_malloc ( size );
	fread ( ptr, 1, size, fp );
	load_source_sound_sample ( sound_sample_index, ptr );
	safe_free ( ptr );
}

/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////


void load_side_dependant_application_sound_samples ( entity_sides side )
{

	if ( application_sound_system_enabled )
	{

		int
			samples_loaded,
			count;

		if ( !command_line_no_sound && !command_line_comms_dedicated_server )
		{

			sound_system_side_loaded = side;

			samples_loaded = FALSE;

			//
			// Load up the blocked sound data if there is some there
			//

			while ( !samples_loaded )
			{

				if ( open_application_sound_file_system ( SOUND_DIRECTORY ) )
				{

					int
						passed;

					//
					// verify the sound data is the stuff we want
					//

					passed = TRUE;

					for ( count = 0; count < number_of_application_sound_samples; count++ )
					{

						int
							sound_block_header_index;

						char
							filename[256];

						if ( application_sound_samples[count].name )
						{

							sprintf ( filename, "%s\\%s", SOUND_DIRECTORY, application_sound_samples[count].name );

							sound_block_header_index = get_sound_block_header_index ( filename, application_sound_samples[count].sound_sample_index );

							//
							// If the sound sample is incorrect - it doesn't match the enumerated type we have to reconstruct the sound block.
							// 	But, if the sound sample is just missing, we can still search for it manually

							if ( sound_block_header_index == SOUND_SAMPLE_INCORRECT )
							{

								debug_log ( "%s is incorrect", filename );

								passed = FALSE;
							}
						}
					}

					//
					// If the data is consistant - we can load them
					//

					if ( passed )
					{

						samples_loaded = TRUE;

						for ( count = 0; count < number_of_application_sound_samples; count++ )
						{

							char
								filename[256];

							int
								sound_sample_index,
								sound_block_header_index;

							sound_sample_index = application_sound_samples[count].sound_sample_index;

							if (	( application_sound_samples[count].side == sound_system_side_loaded ) ||
									( application_sound_samples[count].side == ENTITY_SIDE_NEUTRAL ) ||
									( sound_system_side_loaded == NUM_ENTITY_SIDES ) )
							{

								if ( is_source_sound_sample_valid ( sound_sample_index ) )
								{

									continue;
								}
							}
							else
							{

								if ( is_source_sound_sample_valid ( sound_sample_index ) )
								{

									destroy_source_sound_sample ( sound_sample_index );
								}

								continue;
							}

							if ( application_sound_samples[count].name )
							{
								// SKIP DISABLED MUSIC TYPES TO SAVE MEMORY
								if (count == SOUND_SAMPLE_INDEX_MUSIC_INTRO && !command_line_intro_music ||
									count >= SOUND_SAMPLE_INDEX_MUSIC_INGAME1 && count <= SOUND_SAMPLE_INDEX_MUSIC_INGAME3 && !command_line_ingame_music ||
									count >= SOUND_SAMPLE_INDEX_MUSIC_ACTION1 && count <= SOUND_SAMPLE_INDEX_MUSIC_ACTION3 && !command_line_action_music ||
									count >= SOUND_SAMPLE_INDEX_MUSIC_MISSION_COMPLETE1 && count <= SOUND_SAMPLE_INDEX_MUSIC_MISSION_COMPLETE3 && !command_line_mission_complete_music) {
									sprintf ( filename, "%s\\%s", SOUND_DIRECTORY, application_sound_samples[SOUND_SAMPLE_INDEX_MUSIC_INVALID].name );
								}
								else {
									sprintf ( filename, "%s\\%s", SOUND_DIRECTORY, application_sound_samples[count].name );
								}

								sound_block_header_index = get_sound_block_header_index ( filename, sound_sample_index );

								{
									int
										size;
									char
										wav_filename[260];

									size = strlen ( filename ) - 4;
									memcpy ( wav_filename, filename, size );
									memcpy ( wav_filename + size, ".wav", 5 );

									if ( file_exist ( wav_filename ) )
									{
										struct WAVEHEADER
										{
											char RIFF[4];
											unsigned long rLength;
											char WAVE[4];
											char fmt[4];
											unsigned long fLength;
											unsigned short Format;
											unsigned short Channels;
											unsigned long Frequency;
											unsigned long AvgBytesPerSec;
											unsigned short BlockAlign;
											unsigned short Specific;
											char data[4];
											unsigned long Length;
										};

										FILE
											*fp;
										struct WAVEHEADER
											wh;

										fp = safe_fopen ( wav_filename, "rb" );
										if ( fread ( &wh, sizeof ( wh ), 1, fp ) == 1 )
										{
											if ( !memcmp ( wh.RIFF, "RIFF", 4 ) && !memcmp ( wh.WAVE, "WAVE", 4 ) && !memcmp ( wh.fmt, "fmt ", 4 ) && !memcmp ( wh.data, "data", 4 ) )
											{
												if ( wh.Format != 1 || wh.Channels != 1 || ( wh.Specific != 8 && wh.Specific != 16 ) )
												{
													debug_log ( "Encoding %i Channels %i Bits %i not supported", wh.Format, wh.Channels, wh.Specific );
												}
												else
												{
													#if DEBUG_MODULE
													debug_log ( "Loading WAV file %s Freq %lu Size %lu", wav_filename, wh.Frequency, wh.Length );
													#endif

													load_from_file ( sound_sample_index, wh.Specific == 8 ? SAMPLE_TYPE_MONO_8BIT : SAMPLE_TYPE_MONO_16BIT , wh.Frequency, (int)wh.Length, fp );

													fclose ( fp );

													continue;
												}
											}
										}

										debug_log ( "Invalid WAVE file: %s", wav_filename );

										fclose ( fp );
									}
								}

								//--Werewolf sound override support
								if ( file_exist ( filename ) )
								{
									int
										size;
									FILE
										*fp;

									#if DEBUG_MODULE
									if ( sound_block_header_index == SOUND_SAMPLE_NOT_FOUND )
									{

										debug_log ( "Sound not found in sound.dat: %s", filename );
									}
									else
									{

										debug_log ( "SND_DATA: SOUNDFILE OVERRIDES* found file %s", filename );
									}
									#endif

									size = file_size ( filename );
									fp = safe_fopen ( filename, "rb" );
									load_from_file ( sound_sample_index, SAMPLE_TYPE_MONO_8BIT, application_sound_effects[sound_sample_index].original_rate, size, fp );
									fclose ( fp );
								}
								//-- END Werewolf sound override support
								else
								{
									#if DEBUG_MODULE
									debug_log ( "SND_DATA: Sound loading from sound.dat: %s", filename );
									#endif

									// NO MUSIC FILES CHEAT
									if ( sound_sample_index >= SOUND_SAMPLE_INDEX_MUSIC_INVALID && sound_sample_index <= SOUND_SAMPLE_INDEX_MUSIC_MISSION_COMPLETE3 ) {
										debug_log ( "SND_DATA: Music file not found, music turned off" );
										set_global_sound_music ( GAME_MUSIC_OFF );
										no_music = TRUE;
										
										fseek ( block_sound_sample_data_file, blocked_sound_samples[SOUND_SAMPLE_INDEX_APACHE_LOCK_ON].sound_data_offset, SEEK_SET );
										load_from_file ( sound_sample_index, SAMPLE_TYPE_MONO_8BIT, application_sound_effects[SOUND_SAMPLE_INDEX_APACHE_LOCK_ON].original_rate, blocked_sound_samples[SOUND_SAMPLE_INDEX_APACHE_LOCK_ON].sound_data_length, block_sound_sample_data_file );
									}
									else {
										fseek ( block_sound_sample_data_file, blocked_sound_samples[sound_block_header_index].sound_data_offset, SEEK_SET );
										load_from_file ( sound_sample_index, SAMPLE_TYPE_MONO_8BIT, application_sound_effects[sound_sample_index].original_rate, blocked_sound_samples[sound_block_header_index].sound_data_length, block_sound_sample_data_file );
									}
								}
							}
						}
					}

					//
					// Now close the blocked sound samples file etc
					//

					close_application_sound_file_system ();
				}

				//
				// If we haven't loaded the samples, create the sound block from the array
				//

				if ( !samples_loaded )
				{

					int
						current_blocked_sample;

					FILE
						*fp;

					char
						filename[256];

					current_blocked_sample = 0;

					number_of_blocked_sound_samples = 0;

					//
					// Count number of samples
					//

					#if DEBUG_MODULE
					debug_log ( "CREATING SOUND BLOCK SYSTEM" );
					#endif

					for ( count = 0; count < number_of_application_sound_samples; count++ )
					{

						if ( application_sound_samples[count].name )
						{

							number_of_blocked_sound_samples++;
						}
					}

					//
					// Malloc the headers bit
					//

					blocked_sound_samples = (sound_block_data_header *) safe_malloc ( sizeof ( sound_block_data_header ) * number_of_blocked_sound_samples );

					//
					// Now fill in the data
					//

					sprintf ( filename, "%s\\%s", SOUND_DIRECTORY, SOUND_BLOCK_DATA_FILE );

					block_sound_sample_data_file = safe_fopen ( filename, "wb" );

					for ( count = 0; count < number_of_application_sound_samples; count++ )
					{

						int
							size;

						char
							filename[256];

						if (application_sound_samples [count].name)
						{

							char
								*temp_memory;

							sprintf ( filename, "%s\\%s", SOUND_DIRECTORY, application_sound_samples[count].name );

							size = file_size ( filename );

							fp = safe_fopen ( filename, "rb" );

							temp_memory = ( char * ) safe_malloc ( size );

							fread ( temp_memory, size, 1, fp );

							safe_fclose ( fp );

							//
							// Fill in the header bit
							//

							blocked_sound_samples[current_blocked_sample].sound_sample_index = application_sound_samples[count].sound_sample_index;
							blocked_sound_samples[current_blocked_sample].sound_data_offset = ftell ( block_sound_sample_data_file );
							blocked_sound_samples[current_blocked_sample].sound_data_length = size;
							blocked_sound_samples[current_blocked_sample].filename = ( char * ) safe_malloc ( strlen ( filename ) + 1 );
							strcpy ( blocked_sound_samples[current_blocked_sample].filename, filename );
							current_blocked_sample++;

							//
							// Write the data out
							//

							fwrite ( temp_memory, 1, size, block_sound_sample_data_file );

							//
							// Free the memory
							//

							safe_free ( temp_memory );
						}
					}

					//
					// Close the data file
					//

					safe_fclose ( block_sound_sample_data_file );

					//
					// Write out the headers
					//

					sprintf ( filename, "%s\\%s", SOUND_DIRECTORY, SOUND_BLOCK_HEADER_FILE );

					fp = safe_fopen ( filename, "wb" );

					fwrite ( &number_of_blocked_sound_samples, sizeof ( int ), 1, fp );

					for ( count = 0; count < number_of_blocked_sound_samples; count++ )
					{

						int
							length;

						fwrite ( &blocked_sound_samples[count].sound_sample_index, sizeof ( int ), 1, fp );
						fwrite ( &blocked_sound_samples[count].sound_data_offset, sizeof ( int ), 1, fp );
						fwrite ( &blocked_sound_samples[count].sound_data_length, sizeof ( int ), 1, fp );

						length = strlen ( blocked_sound_samples[count].filename ) + 1;
						fwrite ( &length, sizeof ( int ), 1, fp );

						fwrite ( blocked_sound_samples[count].filename, 1, length, fp );

						//
						// Free the filename up now
						//

						safe_free ( blocked_sound_samples[count].filename );
						blocked_sound_samples[count].filename = NULL;
					}

					fclose ( fp );

					//
					// Free the memory up from the headers
					//

					safe_free ( blocked_sound_samples );
					blocked_sound_samples = NULL;

					number_of_blocked_sound_samples = 0;
				}
			}
		}
	}
}

/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////

int open_application_sound_file_system ( char *directory )
{

	char
		header_filename[256],
		block_filename[256];

	sprintf ( header_filename, "%s\\%s", directory, SOUND_BLOCK_HEADER_FILE );
	sprintf ( block_filename, "%s\\%s", directory, SOUND_BLOCK_DATA_FILE );

	if ( ( file_exist ( header_filename ) ) && ( file_exist ( block_filename ) ) )
	{

		FILE
			*fp;

		int
			count;

		fp = safe_fopen ( header_filename, "rb" );

		fread ( &number_of_blocked_sound_samples, sizeof ( int ), 1, fp );

		blocked_sound_samples = ( sound_block_data_header * ) safe_malloc ( sizeof ( sound_block_data_header ) * number_of_blocked_sound_samples );

		for ( count = 0; count < number_of_blocked_sound_samples; count++ )
		{

			int
				length;

			unsigned int
				hash;

			char
				*ptr;

			fread ( &blocked_sound_samples[count].sound_sample_index, sizeof ( int ), 1, fp );
			fread ( &blocked_sound_samples[count].sound_data_offset, sizeof ( int ), 1, fp );
			fread ( &blocked_sound_samples[count].sound_data_length, sizeof ( int ), 1, fp );
			fread ( &length, sizeof ( int ), 1, fp );

			blocked_sound_samples[count].filename = ( char * ) safe_malloc ( length );
			fread ( blocked_sound_samples[count].filename, 1, length, fp );

			ptr = blocked_sound_samples[count].filename;
			hash = 0;

			while ( *ptr != '\0' )
			{

				hash += *ptr;
				hash <<= 1;
				ptr++;
			}

			blocked_sound_samples[count].hash = hash;
		}

		safe_fclose ( fp );

		block_sound_sample_data_file = safe_fopen ( block_filename, "rb" );

		return ( TRUE );
	}
	else
	{

		return ( FALSE );
	}
}

/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////

void close_application_sound_file_system ( void )
{

	int
		count;

	for ( count = 0; count < number_of_blocked_sound_samples; count++ )
	{

		//
		// Free the filename
		//

		safe_free ( blocked_sound_samples[count].filename );

		blocked_sound_samples[count].filename = NULL;
	}

	//
	// Free the memory up from the headers
	//

	if ( blocked_sound_samples )
	{

		safe_free ( blocked_sound_samples );

		blocked_sound_samples = NULL;
	}

	if ( block_sound_sample_data_file )
	{

		safe_fclose ( block_sound_sample_data_file );
	}
}

/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
/////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////

int get_sound_block_header_index ( char *filename, int sample_index )
{

	int
		count;

	unsigned int
		hash;

	char
		*ptr;

	ASSERT ( filename );

	ptr = filename;

	hash = 0;

	while ( *ptr != '\0' )
	{

		hash += *ptr;

		hash <<= 1;

		ptr++;
	}

	for ( count = 0; count < number_of_blocked_sound_samples; count++ )
	{

		if ( ( blocked_sound_samples[count].filename ) && ( blocked_sound_samples[count].hash == hash ) )
		{

			if ( stricmp ( blocked_sound_samples[count].filename, filename ) == 0 )
			{

				if ( blocked_sound_samples[count].sound_sample_index == sample_index )
				{

					return ( count );
				}
			}
		}
	}

	return ( SOUND_SAMPLE_NOT_FOUND );
}

